function Dashboard_CalculateRating(EnterpriseAccNo, EnterpriseLogo, EnterpriseName) {

  globalFloatPanelMerchantDetailPage_EnterpriseAccNo = EnterpriseAccNo;
  globalFloatPanelMerchantDetailPage_EnterpriseLogo  = EnterpriseLogo;
  globalFloatPanelMerchantDetailPage_EnterpriseName  = EnterpriseName;

  Ext.defer(function () {

    var objn = {
      EnterpriseAccNo: EnterpriseAccNo
    };

    Ext.Ajax.request({
      url: GetAPIurl() + '/AyohaMerchantReview/AyohaMerchantReviewCalculateRating',
      method: 'POST',
      jsonData: objn,
      headers: { "Content-Type": "application/json; charset=utf-8" },
      timeout: 30000,

      callback: function (options, success, response) {
        // Unmask sekali je
        Ext.Viewport.unmask();

        if (!success) {
          // optional debug:
          // console.log('CalculateRating failed', response && response.status, response && response.responseText);
          return;
        }

        var data = Ext.decode((response.responseText || '').trim(), true) || {};
        if (data.success !== "true") return;

        if (!(data.total > 0 && data.results && data.results[0])) {
          // Tiada data rating
          updateDashboardRatingUI(0, 0);
          return;
        }

        var r = data.results[0];

        // Kekalkan assignment global macam asal (kalau code lain guna)
        FiveStar      = r.FiveStar;
        FourStar      = r.FourStar;
        ThreeStar     = r.ThreeStar;
        TwoStar       = r.TwoStar;
        OneStar       = r.OneStar;
        TotalStar     = r.TotalStar;
        TotalVoter    = r.TotalVoter;
        VoteFiveStar  = r.VoteFiveStar;
        VoteFourStar  = r.VoteFourStar;
        VoteThreeStar = r.VoteThreeStar;
        VoteTwoStar   = r.VoteTwoStar;
        VoteOneStar   = r.VoteOneStar;

        var totalVoter = parseFloat(r.TotalVoter) || 0;

        // ikut formula asal kau
        var sumVote =
          (parseFloat(r.VoteFiveStar)  || 0) +
          (parseFloat(r.VoteFourStar)  || 0) +
          (parseFloat(r.VoteThreeStar) || 0) +
          (parseFloat(r.VoteTwoStar)   || 0) +
          (parseFloat(r.VoteOneStar)   || 0);

        var avg = (totalVoter > 0) ? (sumVote / totalVoter) : 0;

        // simpan global (ikut pattern asal)
        globalFloatPanelMerchantDetailPage_CountReviewer = totalVoter;
        globalFloatPanelMerchantDetailPage_CountStar     = (Math.round(avg * 10) / 10).toFixed(1);

        updateDashboardRatingUI(avg, totalVoter);
      }
    });

  }, 500);
}

/* ================= Helpers ================= */

function updateDashboardRatingUI(avg, totalVoter) {
  var rating = isFinite(avg) ? avg : 0;
  var rating1dp = Math.round(rating * 10) / 10; // 1 decimal

  // rating text
  setCmpHtml(
    'htmlDashbord_ReviewAndRateCount_PremiumAdvertisement',
    '<div onclick="FloatPanelMerchantDetailPage_OpenMerchantReview();" ' +
      'style="color:white;text-align:center;font-size:16px;width:100%;font-weight:bold;margin:0;">' +
      rating1dp.toFixed(1) +
    '</div>'
  );

  // total reviews text
  setCmpHtml(
    'htmlDashbord_ReviewByCount_PremiumAdvertisement',
    '<div onclick="FloatPanelMerchantDetailPage_OpenMerchantReview();" ' +
      'style="width:100%;text-align:right;background-color:transparent;font-family:Arial,sans-serif;font-size:8px;color:white;font-weight:bold;overflow:hidden;">' +
      '<img src="resources/icons/myaccountwhite01.png" alt="Image" style="width:7px;height:7px;">&nbsp;' +
      (totalVoter || 0) + ' Reviews' +
    '</div>'
  );

  // stars
  setDashboardStars(rating1dp);
}

function setDashboardStars(rating) {
  for (var i = 1; i <= 5; i++) {
    var frac = rating - (i - 1);     // contoh star ke-3: rating - 2
    var icon = pickStarIcon(frac);   // pilih icon ikut fraction

    setCmpHtml(
      'htmlDashbord_Star' + i,
      '<div onclick="FloatPanelMerchantDetailPage_OpenMerchantReview();">' +
        '<img src="resources/icons/' + icon + '" width="9" height="9" alt="Star">' +
      '</div>'
    );
  }
}

// Mapping icon ikut pecahan star
function pickStarIcon(frac) {
  if (frac >= 0.95) return 'star.png';
  if (frac >= 0.85) return 'StarpointNine.png';
  if (frac >= 0.65) return 'StarpointSeven.png';
  if (frac >= 0.45) return 'StarpointFive.png';
  if (frac >= 0.25) return 'StarpointThree.png';
  return 'reviewstarunrate.png';
}

function setCmpHtml(id, html) {
  var cmp = Ext.getCmp(id);
  if (cmp && cmp.setHtml) cmp.setHtml(html);
}


