function UpdateNotificationUnreadBadge(globalPNUnread){
  var n = parseInt(globalPNUnread || 0, 10);
  var el = document.getElementById('ayohaNotifBadgeUnread');
  if (!el) return;

  if (n > 0){
    el.style.display = 'block';
    el.innerHTML = (n > 99) ? '99+' : String(n);
  } else {
    el.style.display = 'none';
  }
}